import React, { Component } from 'react'
import { TabBarIOS, StyleSheet, Text, View } from 'react-native'

import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'

import CategoryListPge from '../pages/CategoryListPage'
import ManagementPage from '../pages/ManagementPage'
import { switchTab } from '../actions/navigation'

const base64Icon = 'data:image/png;base64,';

class TabView extends Component {
  constructor(props) {
    super()
  }

  onTabSelect(tab) {
    if (this.props.tab !== tab) {
      this.props.onTabSelect(tab);
    }
  }

  render() {
    const { tab, navigator } = this.props;
    return (
      <TabBarIOS
        unselectedTintColor="podManageTab"
        tintColor="#55ACEE"
        barTintColor="white">
        <TabBarIOS.Item
          title='Pods List'
          icon={{uri: base64Icon, scale: 3}}
          selected={tab === 'podListTab'}
          onPress={ this.onTabSelect.bind(this, 'podListTab') }>
          <CategoryListPge navigator={ navigator } />
        </TabBarIOS.Item>

        <TabBarIOS.Item
          title='Pods Manage'
          icon={{uri: base64Icon, scale: 3}}
          style={ styles.tabBar }
          selected={tab === 'podManageTab'}
          onPress={ this.onTabSelect.bind(this, 'podManageTab') }>
          <ManagementPage navigator={ navigator } />
        </TabBarIOS.Item>
      </TabBarIOS>
    )
  }
}

const styles = StyleSheet.create({
  tabContent: {
    flex: 1,
    alignItems: 'center',
  },
  tabText: {
    color: 'white',
    margin: 50,
  },
});

const mapStateToProps = (state) => ({
  tab: state.navigation
})

const mapDispatchToProps = (dispatch) => ({
  onTabSelect: bindActionCreators(switchTab, dispatch)
})


export default connect(mapStateToProps, mapDispatchToProps)(TabView)
